import React, { useState, useContext } from "react";
import {
  TextField,
  Typography,
  Grid,
  CssBaseline,
  Button,
} from "@material-ui/core";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import { v4 as uuidv4 } from "uuid";

import { InfoDialog, GoogleLogIn, HelmetMain, CookiesPolicy } from "common";
import { UserContext } from "hooks";
import { useTranslation } from "react-i18next";

const Register = () => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const { register } = useContext(UserContext);

  const { t } = useTranslation();

  const onSubmit = async (e) => {
    e.preventDefault();
    const user = {
      username: username,
      password: password,
      email: email,
      preonsalURL: uuidv4(),
    };
    await register(user);
  };

  return (
    <React.Fragment>
      <HelmetMain title={t("landig.helmet.register")} />
      <CssBaseline />
      <HelmetMain title={"Ppportfol.io | login"} />
      <Grid className="auth-wrapper">
        <Card className="auth-inner">
          <form onSubmit={onSubmit} validate>
            <Typography
              variant="h3"
              style={{ fontFamily: "Fira Sans", fontWeight: "700" }}
              gutterBottom
            >
              {t("landing.register.title")}
            </Typography>
            <CardContent>
              <TextField
                margin="dense"
                label={t("landing.register.emailLabel")}
                type="email"
                fullWidth
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                variant="outlined"
              />
              <TextField
                margin="dense"
                label={t("landing.register.psswdLabel")}
                type="password"
                fullWidth
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                variant="outlined"
              />

              <Button
                style={{ marginTop: "5px" }}
                color="primary"
                type="submit"
                variant="contained"
              >
                {t("landing.login.registerButton")}
              </Button>
              <p className="text-center">{t("landing.login.googleOption")}</p>

              <GoogleLogIn />
            </CardContent>

            <p className="forgot-password text-right">
              {t("landing.register.alreadyuser")}
              <a href="/index/login">{t("landing.register.loginButton")}</a>
            </p>
          </form>
        </Card>
        <InfoDialog />
      </Grid>
      <CookiesPolicy />
    </React.Fragment>
  );
};

export default Register;
